  }[];
}

export interface GovernanceScore {
  score: number;
  legalCompliance: string;
  esgStatus: string;
  riskFactors: string[];
}

export interface IntelligenceReport {
  masterDraft: string;
  localizedDrafts: Record<string, string>;
  revenueStrategy: string;
  seoForensic: string;
  governance: GovernanceScore;
  blueprint: ImperialBlueprint;
}

import { GoogleGenAI, Type } from "@google/genai";
import { SYSTEM_PROMPT } from "../constants";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY || '' });

export const handleCyborgIngest = async (rawData: string) => {
  const model = 'gemini-3-pro-preview';

  const response = await ai.models.generateContent({
    model,
    contents: `INGEST DATA: ${rawData}\n\nPerform a full Synthesis, Sovereign Analysis, Audit & Governance check.`,
    config: {
      systemInstruction: SYSTEM_PROMPT,
      responseMimeType: "application/json",
      responseSchema: {
        type: Type.OBJECT,
        properties: {
          masterDraft: { type: Type.STRING, description: "WIRED-style strategic thesis." },
          localizedDrafts: {
            type: Type.OBJECT,
            properties: {
              MY: { type: Type.STRING },
              SG: { type: Type.STRING },
              ID: { type: Type.STRING },
              VN: { type: Type.STRING }
            }
          },
          blueprint: {
            type: Type.OBJECT,
            properties: {
              thesis: { type: Type.STRING },
              sectorTrends: {
                type: Type.ARRAY,
                items: {
                  type: Type.OBJECT,
                  properties: {
                    sector: { type: Type.STRING },
                    momentum: { type: Type.STRING },
                    benchmark: { type: Type.STRING }
                  }
                }
              },
              regionalInsights: {
                type: Type.ARRAY,
                items: {
                  type: Type.OBJECT,
                  properties: {
                    market: { type: Type.STRING },
                    strategy: { type: Type.STRING }
                  }
                }
              }
            }
          },
          governance: {
            type: Type.OBJECT,
            properties: {
              score: { type: Type.NUMBER },
              legalCompliance: { type: Type.STRING },
              esgStatus: { type: Type.STRING },
              riskFactors: { type: Type.ARRAY, items: { type: Type.STRING } }
            }
          },
          revenueStrategy: { type: Type.STRING },
          seoForensic: { type: Type.STRING }
        },
        required: ["masterDraft", "blueprint", "governance"]
      }
    }
  });

  return JSON.parse(response.text || '{}');
};

